WHERE {
  ?broadcast a schema:BroadcastEvent ;
             schema:broadcastOfEvent   {event} ;
             ex:usedBroadcastInfrastructure ?infrastructure ;
             ex:distributedVia ?tech .
  OPTIONAL { ?infrastructure rdfs:label ?infraLabel }
