SELECT ?sourceUrl ?exactSelector ?refinedBy
WHERE {
    BIND ({event} AS ?event)
    BIND ({venue} AS ?venue)
    ?annotation a oa:Annotation ;
           dct:subject ex:Performances ;
           schema:spatial ?venue ;
           oa:hasTarget ?event ;
           oa:hasBody ?background .
