PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?eventName ?venueName ?city
WHERE {
    {event} a schema:MusicEvent;
        schema:name ?eventName ;
        schema:location ?venue .
  ?venue schema:name ?venueName .
  ?venue schema:addressLocality ?city .
  FILTER(?venue != ex:Global)
}
ORDER BY ?city

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
SELECT ?eventName (GROUP_CONCAT(DISTINCT ?broadcastingCompanyName; separator=", ") AS ?broadcastingCompanies)
WHERE {
    BIND (  {event} as ?event )
    ?event ex:hadBroadcast ?broadcast .
    ?event schema:name ?eventName .
    OPTIONAL { ?broadcast schema:provider ?broadcastingCompany . }
    OPTIONAL { ?broadcastingCompany schema:name ?broadcastingCompanyName }
    OPTIONAL { ?broadcastingCompany rdfs:label ?broadcastingCompanyName }
}
GROUP BY ?eventName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?eventName ?iconicPerformanceDescription
WHERE {
    ?performance a mm:LivePerformance ;
                 schema:isPartOf ?event ;
    			 ex:isIconic ?isIconic .
    ?performance schema:description ?iconicPerformanceDescription .
    ?event schema:name ?eventName .
    FILTER(?event =   {event})
    FILTER(?isIconic = true)
}

PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX ex: <http://wembrewind.live/ex#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
SELECT ?artistName (GROUP_CONCAT(DISTINCT ?founderName; separator=", ") AS ?foundingMembers)
WHERE {
    BIND ({musicgroup} AS ?artist)
    ?artist a mm:MusicArtist ;
            schema:name ?artistName ;
            schema:foundingDate ?foundingYear .
    ?role a schema:Role, mm:MusicEnsembleMembership ;
          mm:involvesMusicEnsemble ?artist ;
          mm:involvesMemberOfMusicEnsemble ?member ;
          ex:isOriginalMember true .
    ?member schema:name ?founderName .
}
GROUP BY ?artistName
