PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?eventName ?venueName ?city
WHERE {
    {event} a schema:MusicEvent;
        schema:name ?eventName ;
        schema:location ?venue .
  ?venue schema:name ?venueName .
  ?venue schema:addressLocality ?city .
  FILTER(?venue != ex:Global)
}
ORDER BY ?city

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?eventName ?locationName ?cityName ?countryName
WHERE {
    ?event a schema:MusicEvent ;
           schema:location ?location .
    ?event schema:name ?eventName .
    OPTIONAL { ?location schema:location ?city .
        	   ?location schema:name ?locationName .
    			}
    OPTIONAL { ?city schema:containedInPlace ?country .
        		?city schema:name ?cityName .
        		?country schema:name ?countryName .
    			}
    FILTER(?event =   {event})
}

PREFIX ex:     <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT ?artistName ?birthDate ?birthPlace
WHERE {
  BIND({singleartist} AS ?artist)

  ?artist schema:name ?artistName .
  ?artist schema:birthDate ?birthDate .
  OPTIONAL { ?artist schema:birthPlace ?birthPlace }
}

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?artistName ?eventName ?reviewerName ?reviewText ?url
WHERE {
    ?performance schema:performer ?artist ;
                 schema:isPartOf|schema:isPartOf ?event .
    ?artist schema:name ?artistName .
    ?event schema:name ?eventName .
    OPTIONAL {
        ?review schema:itemReviewed ?performance .
        ?review schema:reviewBody ?reviewText .
        ?review schema:author ?reviewer .
        ?reviewer schema:name ?reviewerName .
        ?review schema:url ?url
    }
    OPTIONAL {
        ?performance schema:comment ?audienceComment
    }
    FILTER(?artist =   {musicgroup})
    FILTER(?event =   {event})
}
