PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?eventName ?venueName ?city
WHERE {
    {event} a schema:MusicEvent;
        schema:name ?eventName ;
        schema:location ?venue .
  ?venue schema:name ?venueName .
  ?venue schema:addressLocality ?city .
  FILTER(?venue != ex:Global)
}
ORDER BY ?city

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?eventName (GROUP_CONCAT(DISTINCT ?venueName; separator=", ") AS ?allVenues)
WHERE {
    ?event a schema:MusicEvent ;
           schema:location ?venue .
    ?venue a schema:Place ;
           schema:name ?venueName .
    ?event schema:name ?eventName .
    FILTER(?event =   {event})
}
GROUP BY ?eventName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?eventName ?locationName ?cityName ?countryName
WHERE {
    ?event a schema:MusicEvent ;
           schema:location ?location .
    ?event schema:name ?eventName .
    OPTIONAL { ?location schema:location ?city .
        	   ?location schema:name ?locationName .
    			}
    OPTIONAL { ?city schema:containedInPlace ?country .
        		?city schema:name ?cityName .
        		?country schema:name ?countryName .
    			}
    FILTER(?event =   {event})
}

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?artistName ?debutDate ?debutAlbumName (GROUP_CONCAT(DISTINCT ?featuredSongsName; separator=", ") AS ?albumFeaturedSongs)
WHERE {
    BIND({singleartist} as ?artist)
    ?album a schema:MusicAlbum ;
            schema:byArtist ?artist ;
            ex:isDebutAlbum true .
    ?artist schema:name ?artistName .
    OPTIONAL {?album schema:datePublished ?debutDate .}
    OPTIONAL {?album schema:name ?debutAlbumName .}
    OPTIONAL {?album schema:hasPart ?featuredSongs .}
    OPTIONAL {?featuredSongs schema:name ?featuredSongsName .}
}
GROUP BY ?artistName ?album ?debutDate ?debutAlbumName

PREFIX ex:     <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT ?artistName ?birthDate ?birthPlace
WHERE {
  BIND({singleartist} AS ?artist)

  ?artist schema:name ?artistName .
  ?artist schema:birthDate ?birthDate .
  OPTIONAL { ?artist schema:birthPlace ?birthPlace }
}

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
SELECT ?eventName (GROUP_CONCAT(DISTINCT ?broadcastingCompanyName; separator=", ") AS ?broadcastingCompanies)
WHERE {
    BIND (  {event} as ?event )
    ?event ex:hadBroadcast ?broadcast .
    ?event schema:name ?eventName .
    OPTIONAL { ?broadcast schema:provider ?broadcastingCompany . }
    OPTIONAL { ?broadcastingCompany schema:name ?broadcastingCompanyName }
    OPTIONAL { ?broadcastingCompany rdfs:label ?broadcastingCompanyName }
}
GROUP BY ?eventName
