PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX ex: <http://wembrewind.live/ex#>
SELECT ?eventName ?venueName ?attendance
WHERE {
    BIND(  {event} AS ?event)
    ?event schema:name ?eventName .
    ?subEvent ex:attendance ?attendance ;
              schema:location ?venue .
    OPTIONAL { ?event schema:subEvent ?subEvent }
    OPTIONAL { ?subEvent schema:superEvent ?event }
    FILTER(BOUND(?event))
    ?venue schema:name ?venueName .
}
ORDER BY ?venueName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?eventName (GROUP_CONCAT(DISTINCT ?artistName; separator=", ") AS ?allPerformingArtists)
WHERE {
    ?performance a schema:Event, mm:LivePerformance ;
           schema:isPartOf ?event ;
           schema:performer ?artist .
    ?event schema:name ?eventName .
    ?artist schema:name ?artistName .
    FILTER(?event =   {event})
}
GROUP BY ?eventName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
SELECT DISTINCT ?infrastructureName ?tech
WHERE {
  ?broadcast a schema:BroadcastEvent ;
             schema:broadcastOfEvent   {event} ;
             ex:usedBroadcastInfrastructure ?infrastructure ;
             ex:distributedVia ?tech .
  OPTIONAL { ?infrastructure rdfs:label ?infraLabel }
  OPTIONAL { ?infrastructure schema:name ?infraNameRaw }
  BIND(COALESCE(?infraLabel, ?infraNameRaw, STRAFTER(STR(?infrastructure), "#")) AS ?infrastructureName)
}
ORDER BY ?infrastructureName ?techName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?instrumentName
WHERE {
  ?membership a schema:Role,
        mm:MusicEnsembleMembership ;
        schema:instrument ?instrument .
  OPTIONAL { ?instrument schema:name ?instrumentName }
}
ORDER BY ?instrumentName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?eventName ?currency ?raisedAmountValue
WHERE {
    VALUES ?event {   {event} }
    ?event ex:raisedAmount ?raisedAmount .
    ?amount a schema:MonetaryAmount .
    ?event schema:name ?eventName .
    OPTIONAL { ?amount schema:currency ?currency }
    OPTIONAL { ?amount schema:value ?raisedAmountValue }
}

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
SELECT ?eventName (GROUP_CONCAT(DISTINCT ?broadcastingCompanyName; separator=", ") AS ?broadcastingCompanies)
WHERE {
    BIND (  {event} as ?event )
    ?event ex:hadBroadcast ?broadcast .
    ?event schema:name ?eventName .
    OPTIONAL { ?broadcast schema:provider ?broadcastingCompany . }
    OPTIONAL { ?broadcastingCompany schema:name ?broadcastingCompanyName }
    OPTIONAL { ?broadcastingCompany rdfs:label ?broadcastingCompanyName }
}
GROUP BY ?eventName
