PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?eventName ?venueName ?city
WHERE {
    {event} a schema:MusicEvent;
        schema:name ?eventName ;
        schema:location ?venue .
  ?venue schema:name ?venueName .
  ?venue schema:addressLocality ?city .
  FILTER(?venue != ex:Global)
}
ORDER BY ?city

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?eventName (GROUP_CONCAT(DISTINCT ?artistName; separator=", ") AS ?allPerformingArtists)
WHERE {
    ?performance a schema:Event, mm:LivePerformance ;
           schema:isPartOf ?event ;
           schema:location ?venue ;
           schema:performer ?artist .
    ?event schema:name ?eventName .
    ?artist schema:name ?artistName .
    FILTER(?event =   {event})
    FILTER(?venue = {venue})
}
GROUP BY ?eventName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
SELECT ?eventName (GROUP_CONCAT(DISTINCT ?broadcastingCompanyName; separator=", ") AS ?broadcastingCompanies)
WHERE {
    BIND (  {event} as ?event )
    ?event ex:hadBroadcast ?broadcast .
    ?event schema:name ?eventName .
    OPTIONAL { ?broadcast schema:provider ?broadcastingCompany . }
    OPTIONAL { ?broadcastingCompany schema:name ?broadcastingCompanyName }
    OPTIONAL { ?broadcastingCompany rdfs:label ?broadcastingCompanyName }
}
GROUP BY ?eventName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?eventName ?currency ?raisedAmountValue
WHERE {
    VALUES ?event {   {event} }
    ?event ex:raisedAmount ?raisedAmount .
    ?amount a schema:MonetaryAmount .
    ?event schema:name ?eventName .
    OPTIONAL { ?amount schema:currency ?currency }
    OPTIONAL { ?amount schema:value ?raisedAmountValue }
}

PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX ex: <http://wembrewind.live/ex#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
SELECT ?artistName (GROUP_CONCAT(DISTINCT ?founderName; separator=", ") AS ?foundingMembers)
WHERE {
    BIND ({musicgroup} AS ?artist)
    ?artist a mm:MusicArtist ;
            schema:name ?artistName ;
            schema:foundingDate ?foundingYear .
    ?role a schema:Role, mm:MusicEnsembleMembership ;
          mm:involvesMusicEnsemble ?artist ;
          mm:involvesMemberOfMusicEnsemble ?member ;
          ex:isOriginalMember true .
    ?member schema:name ?founderName .
}
GROUP BY ?artistName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?artistName (GROUP_CONCAT(DISTINCT ?genreLabel; separator=", ") AS ?allGenres)
WHERE {
    ?artist schema:name ?artistName .
    { ?artist schema:genre ?genre } UNION { ?artist mm:hasGenre ?genre } .
    OPTIONAL { ?genre schema:name ?genreLabel . }
    OPTIONAL { ?genre rdfs:label ?genreLabel . }
    BIND(STRAFTER(STR(?genre), "#") AS ?localName)
    FILTER(?artist =   {musicgroup})
}
GROUP BY ?artistName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?artistName (GROUP_CONCAT(DISTINCT ?memberName; separator=", ") AS ?allMembers)
WHERE {
    ?artist schema:name ?artistName .
    OPTIONAL {
        ?artist schema:member ?member .
        ?member schema:name ?memberName .
    }
    FILTER(?artist =   {musicgroup})
}
GROUP BY ?artistName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
SELECT ?eventName ?subBroadcastDescription ?broadcastCompanyName ?preserved
WHERE {
    VALUES ?mainEvent {   {event} }
    ?mainEvent ex:hadBroadcast ?mainBroadcast .
    ?mainEvent schema:name ?eventName .
    ?mainBroadcast schema:subEvent ?subBroadcast .
    ?subBroadcast schema:description ?subBroadcastDescription .
    ?subBroadcast schema:provider ?broadcastCompany .
    ?subBroadcast ex:wasBroadcastPreserved ?preserved .
    OPTIONAL { ?broadcastCompany schema:name ?broadcastCompanyName }
    OPTIONAL { ?broadcastCompany rdfs:label ?broadcastCompanyName }
}
ORDER BY DESC(?preserved)
