PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT ?eventName ?venueName ?city
WHERE {
    {event} a schema:MusicEvent;
        schema:name ?eventName ;
        schema:location ?venue .
  ?venue schema:name ?venueName .
  ?venue schema:addressLocality ?city .
  FILTER(?venue != ex:Global)
}
ORDER BY ?city

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?eventName (GROUP_CONCAT(DISTINCT ?artistName; separator=", ") AS ?allPerformingArtists)
WHERE {
    ?performance a schema:Event, mm:LivePerformance ;
           schema:isPartOf ?event ;
           schema:location ?venue ;
           schema:performer ?artist .
    ?event schema:name ?eventName .
    ?artist schema:name ?artistName .
    FILTER(?event =   {event})
    FILTER(?venue = {venue})
}
GROUP BY ?eventName

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT DISTINCT ?eventName ?currency ?raisedAmountValue
WHERE {
    VALUES ?event {   {event} }
    ?event ex:raisedAmount ?raisedAmount .
    ?amount a schema:MonetaryAmount .
    ?event schema:name ?eventName .
    OPTIONAL { ?amount schema:currency ?currency }
    OPTIONAL { ?amount schema:value ?raisedAmountValue }
}

PREFIX ex:     <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm:     <https://w3id.org/polifonia/ontology/music-meta/>
PREFIX rdfs:   <http://www.w3.org/2000/01/rdf-schema#>
SELECT ?artistName ?venueDisplay ?performanceStartTime
WHERE {
  BIND ({event} AS ?event)
  VALUES ?venue { {venue} {venue2} }

  ?performance a mm:LivePerformance ;
               schema:isPartOf ?event ;
               schema:performer ?artist ;
               schema:location ?venue .

  # Start time (pick whichever exists)
  OPTIONAL { ?performance schema:startDateTime ?sdt }
  OPTIONAL { ?performance schema:startDate ?sd }
  OPTIONAL { ?performance schema:startTime ?st }
  BIND(COALESCE(?sdt, ?sd, ?st) AS ?performanceStartTime)

  OPTIONAL { ?artist schema:name ?artistName }
  OPTIONAL { ?venue schema:name ?venueName }
  OPTIONAL { ?venue rdfs:label ?venueLabel }
  BIND(COALESCE(?venueName, ?venueLabel, STRAFTER(STR(?venue), "#")) AS ?venueDisplay)
}
ORDER BY ?performanceStartTime

PREFIX ex: <http://wembrewind.live/ex#>
PREFIX schema: <http://schema.org/>
PREFIX mm: <https://w3id.org/polifonia/ontology/music-meta/>
SELECT ?performance ?reason
WHERE {
    ?performance a mm:LivePerformance ;
                 schema:isPartOf   {event} ;
                 schema:performer   {musicgroup} .
    ?review schema:itemReviewed ?performance .
    ?review schema:reviewBody ?reason .
}
